function onOpen() {
  SpreadsheetApp.getUi().createMenu("Cat√°logo")
    .addItem("üìë Abrir men√∫", "openSidebar")
    .addItem("üîì Activar acceso", "activateAccess")
    .addItem("üõü Ayuda", "helpModal")
    .addToUi();
}

function openSidebar() {
  Sidebar.show();
}

// =====================
// üìÅ Sidebar.gs (UI handling)
// =====================
const Sidebar = {
  show: function () {
    const html = HtmlService.createHtmlOutputFromFile("mainNav")
      .setTitle("Cat√°logo");
    SpreadsheetApp.getUi().showSidebar(html);
  },
};

function getSheetButtons() {
  const hidden = ["DropdownData"];
  return SpreadsheetApp.getActiveSpreadsheet().getSheets()
    .filter(s => !hidden.includes(s.getName()))
    .map(s => `<li><button class="sheet-button" onclick="goToSheet('${s.getName()}')">${s.getName()}</button></li>`) 
    .join("");
}

function switchSheet(sheetName) {
  const sheet = SpreadsheetApp.getActiveSpreadsheet().getSheetByName(sheetName);
  if (sheet) SpreadsheetApp.getActiveSpreadsheet().setActiveSheet(sheet);
  return sheet.getName();
}

function getActiveSheetName() {
  return SpreadsheetApp.getActiveSpreadsheet().getActiveSheet().getName();
}

function reloadSheetList() {
  return getSheetButtons();
}

// =====================
// üìÅ Triggers.gs (user activation and installable triggers)
// =====================

function activateAccess() {
  ScriptApp.newTrigger("onOpenInstallable")
    .forSpreadsheet(SpreadsheetApp.getActiveSpreadsheet())
    .onOpen()
    .create();
  ScriptApp.newTrigger("onChangeTrigger")
    .forSpreadsheet(SpreadsheetApp.getActiveSpreadsheet())
    .onChange()
    .create();
  SpreadsheetApp.getUi().alert("‚úÖ Acceso activado. Recarga la hoja para usar la app correctamente.");
}

function showHelpDialog(fileName, title) {
  const html = HtmlService.createHtmlOutputFromFile(fileName)
      .setWidth(800)
      .setHeight(800);
  SpreadsheetApp.getUi().showModalDialog(html, title);
}
function helpModal() {
  showHelpDialog("Ayuda", "üõü Ayuda");
}
function helpPermisos() {
  showHelpDialog("activarPermisos", "Activar Permisos");
}
function helpNewSheet() {
  showHelpDialog("newSheet", "C√≥mo crear una nueva Hoja de C√°lculo");
}
function helpIMGs() {
  showHelpDialog("imgURLs", "Cargar y Configurar im√°genes");
}
function helpNewFigma() {
  showHelpDialog("newFigma", "Crear nuevo archivo de Figma");
}
function helpPDF() {
  showHelpDialog("generarPDF", "Generar un Cat√°logo en PDF");
}


function onOpenInstallable() {
  Sidebar.show();
  storeSheetList();
  updateDropdownValues();
}

function onChangeTrigger(e) {
  const ss = SpreadsheetApp.getActiveSpreadsheet();
  const current = ss.getSheets().map(s => s.getName());
  const stored = JSON.parse(PropertiesService.getScriptProperties().getProperty("sheetList") || "[]");

  if (stored.length !== current.length) {
    Logger.log("Sheet structure changed. Updated stored list.");
  }

  storeSheetList();
}

function storeSheetList() {
  const names = SpreadsheetApp.getActiveSpreadsheet().getSheets().map(s => s.getName());
  PropertiesService.getScriptProperties().setProperty("sheetList", JSON.stringify(names));
}

// =====================
// üìÅ SheetsManager.gs (add/duplicate/delete logic)
// =====================

function nuevaFicha() {
  const templateId = "14uyMWHcj1Oex1xDjY9KZ5acP-ZioCR7xDPX6dfhbPqM";
  const templateSheet = SpreadsheetApp.openById(templateId).getSheetByName("Plantilla");
  if (!templateSheet) return SpreadsheetApp.getUi().alert("No se encontr√≥ la plantilla.");

  const ss = SpreadsheetApp.getActiveSpreadsheet();
  const newSheet = templateSheet.copyTo(ss);
  newSheet.setName(generateNextFichaName(ss));
  ss.setActiveSheet(newSheet);

  copyProtections(newSheet);
  SpreadsheetApp.flush();

  return true;
}

function duplicarFicha() {
  const ss = SpreadsheetApp.getActiveSpreadsheet();
  const active = ss.getActiveSheet();
  const copy = active.copyTo(ss);
  copy.setName(generateNextFichaName(ss));
  ss.setActiveSheet(copy);

  copyProtections(copy);
  SpreadsheetApp.flush();

  return true;
}

function eliminarFicha(callbackFnName) {
  const ss = SpreadsheetApp.getActiveSpreadsheet();
  const sheet = ss.getActiveSheet();
  const allSheets = ss.getSheets();
  const visibleSheets = allSheets.filter(s => s.getName() !== "DropdownData");

  if (visibleSheets.length <= 1) {
    return SpreadsheetApp.getUi().alert("No puedes eliminar la √∫ltima ficha.");
  }

  const confirm = SpreadsheetApp.getUi().alert(
    "Eliminar Ficha",
    `¬øEliminar la ficha '${sheet.getName()}'?`,
    SpreadsheetApp.getUi().ButtonSet.YES_NO
  );

  if (confirm === SpreadsheetApp.getUi().Button.YES) {
    ss.deleteSheet(sheet);

    const safeSheet = ss.getSheets().find(s => s.getName() !== "DropdownData");
    if (safeSheet) ss.setActiveSheet(safeSheet);

    hideInternalSheets();
    SpreadsheetApp.flush();

    // üß† Don‚Äôt try to reopen the sidebar with just a script tag ‚Äî instead, return a success flag
    return true;
  }
}

function generateNextFichaName(ss) {
  const base = "Ficha";
  const numbers = ss.getSheets()
    .map(s => s.getName().match(/Ficha (\d{2})$/))
    .filter(Boolean)
    .map(m => parseInt(m[1])) || [];
  const next = numbers.length ? Math.max(...numbers) + 1 : 1;
  return `${base} ${String(next).padStart(2, "0")}`;
}

function copyProtections(sheet) {
  const unprotectedRanges = [
    "Q26:T26",
    "V26:AD26",
    "F10:H16",
    "J10:L16",
    "F18:H18",
    "J18:L18",
    "P16:AD20",
    "AF16:AT20",
    "AF3:AN3",
    "F3:L5",
    "F20:H20",
    "C26:H26",
    "J26:026",
    "AF26:AH26",
    "AJ26:AL26",
    "AN26:AP26",
    "AL31:AP31",
    "AR31:AT31",
    "AR26:AT26",
    "C31:G31",
    "I31:L31",
    "N31:R31",
    "T31:W31",
    "AA31:AE31",
    "AG31:AJ31",
    "AH12:AO12",
    "AH7:AO7"
  ];
  const ranges = unprotectedRanges.map(r => sheet.getRange(r));
  const protection = sheet.protect().setDescription("Protecci√≥n general");
  protection.setUnprotectedRanges(ranges);
  protection.addEditor(Session.getEffectiveUser());
}

function hideInternalSheets() {
  const ss = SpreadsheetApp.getActiveSpreadsheet();
  const internalSheets = ["DropdownData"]; // Add more if needed

  internalSheets.forEach(name => {
    const sheet = ss.getSheetByName(name);
    if (sheet && !sheet.isSheetHidden()) {
      sheet.hideSheet();
    }
  });
}

// =====================
// üìÅ ImportDropdowns.gs
// =====================

function updateDropdownValues() {
  const sourceId = '1vCBRmNE4DXcsvvNt_8QFKMwQOe_gTRakGxxGuEM7t_A';
  const sourceSheet = SpreadsheetApp.openById(sourceId).getSheetByName('Misc');
  const sourceValues = sourceSheet.getRange('A1:C').getValues();

  const converted = sourceValues.map(row => {
    const match = row[1].match(/\/d\/([^\/]+)\//);
    row[1] = match ? `https://drive.google.com/uc?export=view&id=${match[1]}` : "";
    return row;
  });

  const ss = SpreadsheetApp.getActiveSpreadsheet();
  const target = ss.getSheetByName("DropdownData") || ss.insertSheet("DropdownData");
  target.clear();
  target.getRange(1, 1, converted.length, 3).setValues(converted);
  target.hideSheet();
}

function updateInputStyles(sheet) {
  // Use active sheet if none passed
  sheet = sheet || SpreadsheetApp.getActiveSpreadsheet().getActiveSheet();

  const sheetName = sheet.getName();
  if (!/^Ficha \d{2}$/.test(sheetName)) return;

  // 1Ô∏è‚É£ Handle checkboxes
  const pairs = [
    { checkbox: "C31", input: "I31:L31" },
    { checkbox: "AA31", input: "AG31:AJ31" },
    { checkbox: "AL31", input: "AR31:AT31" }
  ];

  pairs.forEach(pair => {
    const value = sheet.getRange(pair.checkbox).getValue();
    const input = sheet.getRange(pair.input);
    input.setBackground(value ? "#ffffff" : "#f8faf8");
    input.setFontColor(value ? "#000000" : "#999999");
  });

  // 2Ô∏è‚É£ Handle engine type changes
  const engineType = sheet.getRange("C26").getValue().toLowerCase().trim();
  const isElectric = engineType === "el√©ctrico";

  // Update fuel/autonomy labels
  sheet.getRange("AI24:AM24").setValue(isElectric ? "Autonom√≠a WLTP" : "Dep√≥sito");
  sheet.getRange("AJ25:AL25").setValue(isElectric ? "km" : "l");
  sheet.getRange("AR25:AT25").setValue(isElectric ? "kWh/100km" : "l/100km");
}

function onEdit(e) {
  if (!e || !e.range) return;
  updateInputStyles(e.range.getSheet());
}

function SPREADSHEETNAME() {
  const name = SpreadsheetApp.getActiveSpreadsheet().getName();
  const parts = name.split("|");
  const clean = parts.length > 1 ? parts[1].trim() : name;
  return encodeURIComponent(clean);
}
